// src/components/ItemList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const { data } = await axios.get('http://3.29.24.171:3001/api/items');
        setItems(data);
      } catch (err) {
        setError('Failed to fetch items.');
      }
    };
    fetchItems();
  }, []);

  return (
    <div className="container">
      <h1>Item List</h1>
      {error && <p className="error">{error}</p>}
      <table className="item-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.description}</td>
              <td>
                <Link to={`/edit/${item._id}`} className="btn">Edit</Link>
                <Link to={`/delete/${item._id}`} className="btn btn-danger">Delete</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to="/create" className="btn btn-primary">Create New Item</Link>
    </div>
  );
};

export default ItemList;
