// src/components/ItemList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://3.29.24.171:3001/api/items');
        setItems(response.data);
      } catch (err) {
        setError('Failed to fetch items.');
      }
    };

    fetchItems();
  }, []);

  return (
    <div className="container">
      <h1>Item List</h1>
      {error && <p className="error">{error}</p>}
      <ul className="item-list">
        {items.length > 0 ? (
          items.map(item => (
            <li key={item._id} className="item">
              <h2>{item.name}</h2>
              <p>{item.description}</p>
              <Link to={`/edit/${item._id}`} className="btn">Edit</Link>
              <Link to={`/delete/${item._id}`} className="btn btn-danger">Delete</Link>
            </li>
          ))
        ) : (
          <p>No items available</p>
        )}
      </ul>
      <Link to="/create" className="btn btn-primary">Create New Item</Link>
    </div>
  );
};

export default ItemList;
